import React, { useState } from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios';

const API_BASE_URL = 'http://104.211.219.98/train';

const App = () => {
  const [companyName, setCompanyName] = useState('');
  const [clientID, setClientID] = useState('');
  const [clientSecret, setClientSecret] = useState('');
  const [accessToken, setAccessToken] = useState('');

  // Step 1: Register your company
  const registerCompany = async () => {
    try {
      const registrationData = {
        companyName: 'Your Company Name',
        ownerName: 'Owner Name',
        rollNo: 'Your Roll Number',
        ownerEmail: 'Owner Email',
        accessCode: 'Access Code',
      };

      const response = await axios.post(`${API_BASE_URL}/register`, registrationData);
      const { companyName, clientID, clientSecret } = response.data;

      setCompanyName(companyName);
      setClientID(clientID);
      setClientSecret(clientSecret);
    } catch (error) {
      console.error('Company registration failed:', error);
    }
  };

  // Step 2: Obtain the authorization token
  const getAuthorizationToken = async () => {
    try {
      const authData = {
        companyName,
        clientID,
        ownerName: 'Owner Name',
        ownerEmail: 'Owner Email',
        rollNo: 'Your Roll Number',
        clientSecret,
      };

      const response = await axios.post(`${API_BASE_URL}/auth`, authData);
      const { accessToken } = response.data;

      setAccessToken(accessToken);
    } catch (error) {
      console.error('Authorization token retrieval failed:', error);
    }
  };

  useEffect(() => {
    registerCompany();
  }, []);

  useEffect(() => {
    if (clientID && clientSecret) {
      getAuthorizationToken();
    }
  }, [clientID, clientSecret]);


  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="companyName">Company Name</label>
        <input
          type="text"
          className="form-control"
          id="companyName"
          name="companyName"
          value={formData.companyName}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="ownerName">Owner Name</label>
        <input
          type="text"
          className="form-control"
          id="ownerName"
          name="ownerName"
          value={formData.ownerName}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="rollno">Roll No</label>
        <input
          type="text"
          className="form-control"
          id="rollno"
          name="rollno"
          value={formData.rollno}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="ownerEmail">Owner Email</label>
        <input
          type="email"
          className="form-control"
          id="ownerEmail"
          name="ownerEmail"
          value={formData.ownerEmail}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="accessCode">Access Code</label>
        <input
          type="text"
          className="form-control"
          id="accessCode"
          name="accessCode"
          value={formData.accessCode}
          onChange={handleChange}
          required
        />
      </div>

      <button type="submit" className="btn btn-primary">
        Submit
      </button>
    </form>
  );
};

export default FormComponent;
